changeSet.sql:
UPDATE pull_request_review_thread
SET provider_thread_id = id
WHERE provider_thread_id IS NULL;

DO $$
DECLARE
    rec RECORD;
    mismatch_count integer;
BEGIN
    SELECT COUNT(*)
    INTO mismatch_count
    FROM issue
    WHERE (body IS NOT NULL AND body_text IS NULL)
       OR (bad_practice_summary IS NOT NULL AND bad_practice_summary_text IS NULL);

    IF mismatch_count > 0 THEN
        RAISE EXCEPTION 'Failed to migrate issue large objects: % rows missing', mismatch_count;
    END IF;

    FOR rec IN
        SELECT body, bad_practice_summary FROM issue
        WHERE body IS NOT NULL OR bad_practice_summary IS NOT NULL
    LOOP
        IF rec.body IS NOT NULL AND EXISTS (SELECT 1 FROM pg_largeobject_metadata lom WHERE lom.oid = rec.body) THEN
            PERFORM pg_catalog.lo_unlink(rec.body);
        END IF;
        IF rec.bad_practice_summary IS NOT NULL AND EXISTS (SELECT 1 FROM pg_largeobject_metadata lom WHERE lom.oid = rec.bad_practice_summary) THEN
            PERFORM pg_catalog.lo_unlink(rec.bad_practice_summary);
        END IF;
    END LOOP;
END$$;

