ALTER TABLE pull_request_review_comment
    ADD COLUMN body_text TEXT,
    ADD COLUMN diff_hunk_text TEXT;

DO $$
DECLARE
    rec RECORD;
    mismatch_count integer;
BEGIN
    SELECT COUNT(*)
    INTO mismatch_count
    FROM pull_request_review_comment
    WHERE (body IS NOT NULL AND body_text IS NULL)
       OR (diff_hunk IS NOT NULL AND diff_hunk_text IS NULL);

    IF mismatch_count > 0 THEN
        RAISE EXCEPTION 'Failed to migrate pull_request_review_comment large objects: % rows missing', mismatch_count;
    END IF;

    FOR rec IN
        SELECT body, diff_hunk FROM pull_request_review_comment
        WHERE body IS NOT NULL OR diff_hunk IS NOT NULL
    LOOP
        IF rec.body IS NOT NULL AND EXISTS (SELECT 1 FROM pg_largeobject_metadata lom WHERE lom.oid = rec.body) THEN
            PERFORM pg_catalog.lo_unlink(rec.body);
        END IF;
        IF rec.diff_hunk IS NOT NULL AND EXISTS (SELECT 1 FROM pg_largeobject_metadata lom WHERE lom.oid = rec.diff_hunk) THEN
            PERFORM pg_catalog.lo_unlink(rec.diff_hunk);
        END IF;
    END LOOP;
END$$;

