changeSet.sql:
ALTER TABLE activity_event
ADD CONSTRAINT chk_activity_event_xp_non_negative
CHECK (xp >= 0);

ALTER TABLE dead_letter_event
ADD CONSTRAINT chk_dead_letter_status
CHECK (status IN ('PENDING', 'RESOLVED', 'DISCARDED'));

-- Migrate state column (PullRequestBadPracticeState enum) ALTER TABLE pullrequestbadpractice ALTER COLUMN state TYPE VARCHAR(32) USING CASE state::integer WHEN 0 THEN 'GOOD_PRACTICE' WHEN 1 THEN 'MINOR_ISSUE' WHEN 2 THEN 'NORMAL_ISSUE' WHEN 3 THEN 'CRITICAL_ISSUE' WHEN 4 THEN 'FIXED' WHEN 5 THEN 'WONT_FIX' WHEN 6 THEN 'WRONG' ELSE 'NORMAL_ISSUE' END;
-- Migrate user_state column (PullRequestBadPracticeState enum) ALTER TABLE pullrequestbadpractice ALTER COLUMN user_state TYPE VARCHAR(32) USING CASE user_state::integer WHEN 0 THEN 'GOOD_PRACTICE' WHEN 1 THEN 'MINOR_ISSUE' WHEN 2 THEN 'NORMAL_ISSUE' WHEN 3 THEN 'CRITICAL_ISSUE' WHEN 4 THEN 'FIXED' WHEN 5 THEN 'WONT_FIX' WHEN 6 THEN 'WRONG' ELSE NULL END;
-- Migrate detection_pullrequest_lifecycle_state column (PullRequestLifecycleState enum) ALTER TABLE pullrequestbadpractice ALTER COLUMN detection_pullrequest_lifecycle_state TYPE VARCHAR(32) USING CASE detection_pullrequest_lifecycle_state::integer WHEN 0 THEN 'DRAFT' WHEN 1 THEN 'OPEN' WHEN 2 THEN 'READY_FOR_REVIEW' WHEN 3 THEN 'READY_TO_MERGE' WHEN 4 THEN 'MERGED' WHEN 5 THEN 'CLOSED' ELSE NULL END;

DELETE FROM repository
WHERE id IN (
    SELECT id FROM (
        SELECT id,
               ROW_NUMBER() OVER (
                   PARTITION BY name_with_owner
                   ORDER BY updated_at DESC NULLS LAST, id DESC
               ) as rn
        FROM repository
        WHERE name_with_owner IS NOT NULL
    ) ranked
    WHERE rn > 1
);

-- Disable triggers temporarily for faster truncation
SET session_replication_role = 'replica';

-- Wipe activity events (will be regenerated during sync)
TRUNCATE TABLE activity_event CASCADE;

-- Wipe workspace team settings (will be regenerated from teams)
TRUNCATE TABLE workspace_team_label_filter CASCADE;
TRUNCATE TABLE workspace_team_repository_settings CASCADE;
TRUNCATE TABLE workspace_team_settings CASCADE;

-- Wipe bad practice data (will be regenerated by detection)
TRUNCATE TABLE pull_request_bad_practice CASCADE;
TRUNCATE TABLE bad_practice_detection CASCADE;

-- Wipe PR review data (CASCADE handles dependencies)
TRUNCATE TABLE pull_request_review_comment CASCADE;
TRUNCATE TABLE pull_request_review_thread CASCADE;
TRUNCATE TABLE pull_request_review CASCADE;

-- Wipe issue data
TRUNCATE TABLE issue_comment CASCADE;
TRUNCATE TABLE issue_blocking CASCADE;
TRUNCATE TABLE issue CASCADE;  -- Includes pull_request via STI

-- Wipe supporting entities
TRUNCATE TABLE milestone CASCADE;
TRUNCATE TABLE label CASCADE;
TRUNCATE TABLE issue_type CASCADE;

-- Wipe team data
TRUNCATE TABLE team_membership CASCADE;
TRUNCATE TABLE team_repository_permission CASCADE;
TRUNCATE TABLE team CASCADE;

-- Wipe repository data
TRUNCATE TABLE repository_to_monitor CASCADE;
TRUNCATE TABLE repository_collaborator CASCADE;
TRUNCATE TABLE repository CASCADE;

-- Wipe organization data
-- IMPORTANT: Unlink workspaces first to prevent CASCADE from deleting workspace/membership data
UPDATE workspace SET organization_id = NULL;
TRUNCATE TABLE organization CASCADE;

-- Re-enable triggers
SET session_replication_role = 'origin';

-- Log the count of records now in workspace_team_repository_settings
-- This is purely for verification and does not modify data
DO $$
DECLARE
    record_count INTEGER;
BEGIN
    SELECT COUNT(*) INTO record_count
    FROM workspace_team_repository_settings
    WHERE hidden_from_contributions = true;

    RAISE NOTICE 'Migration verification: % records with hidden_from_contributions=true in workspace_team_repository_settings', record_count;
END $$;

-- Create temp table mapping duplicate issues to their keeper CREATE TEMP TABLE issue_merge_map AS SELECT duplicate_id, keeper_id FROM ( SELECT i.id AS duplicate_id, FIRST_VALUE(i.id) OVER ( PARTITION BY i.repository_id, i.number ORDER BY i.updated_at DESC NULLS LAST, i.id DESC ) AS keeper_id FROM issue i WHERE i.repository_id IS NOT NULL AND i.number IS NOT NULL AND (i.repository_id, i.number) IN ( SELECT repository_id, number FROM issue WHERE repository_id IS NOT NULL GROUP BY repository_id, number HAVING COUNT(*) > 1 ) ) ranked WHERE duplicate_id != keeper_id;
-- Only proceed if there are duplicates DO $$ BEGIN IF EXISTS (SELECT 1 FROM issue_merge_map) THEN -- Handle junction tables (delete referencing rows for duplicates) -- issue_label: Delete label associations for duplicate issues DELETE FROM issue_label il WHERE EXISTS (SELECT 1 FROM issue_merge_map m WHERE il.issue_id = m.duplicate_id);
-- issue_assignee: Delete assignee associations for duplicate issues DELETE FROM issue_assignee ia WHERE EXISTS (SELECT 1 FROM issue_merge_map m WHERE ia.issue_id = m.duplicate_id);
-- pull_request_requested_reviewers: Delete for duplicate PRs DELETE FROM pull_request_requested_reviewers prr WHERE EXISTS (SELECT 1 FROM issue_merge_map m WHERE prr.pull_request_id = m.duplicate_id);
-- Handle FK references by re-pointing to keeper -- issue_comment UPDATE issue_comment SET issue_id = m.keeper_id FROM issue_merge_map m WHERE issue_comment.issue_id = m.duplicate_id;
-- pull_request_review UPDATE pull_request_review SET pull_request_id = m.keeper_id FROM issue_merge_map m WHERE pull_request_review.pull_request_id = m.duplicate_id;
-- pull_request_review_comment UPDATE pull_request_review_comment SET pull_request_id = m.keeper_id FROM issue_merge_map m WHERE pull_request_review_comment.pull_request_id = m.duplicate_id;
-- pull_request_review_thread UPDATE pull_request_review_thread SET pull_request_id = m.keeper_id FROM issue_merge_map m WHERE pull_request_review_thread.pull_request_id = m.duplicate_id;
-- bad_practice_detection UPDATE bad_practice_detection SET pullrequest_id = m.keeper_id FROM issue_merge_map m WHERE bad_practice_detection.pullrequest_id = m.duplicate_id;
-- pull_request_bad_practice UPDATE pull_request_bad_practice SET pullrequest_id = m.keeper_id FROM issue_merge_map m WHERE pull_request_bad_practice.pullrequest_id = m.duplicate_id;
-- issue_blocking (both columns reference issue) UPDATE issue_blocking SET blocked_issue_id = m.keeper_id FROM issue_merge_map m WHERE issue_blocking.blocked_issue_id = m.duplicate_id;
UPDATE issue_blocking SET blocking_issue_id = m.keeper_id FROM issue_merge_map m WHERE issue_blocking.blocking_issue_id = m.duplicate_id;
-- issue.parent_issue_id (self-reference) UPDATE issue SET parent_issue_id = m.keeper_id FROM issue_merge_map m WHERE issue.parent_issue_id = m.duplicate_id;
-- Now delete the duplicate issues DELETE FROM issue WHERE id IN (SELECT duplicate_id FROM issue_merge_map); END IF; END $$;
-- Clean up temp table DROP TABLE IF EXISTS issue_merge_map;

-- First, update any foreign key references to point to the team we're keeping -- TeamMembership references UPDATE team_membership tm SET team_id = ( SELECT keeper_id FROM ( SELECT t1.organization, t1.name, (SELECT id FROM team t2 WHERE t2.organization = t1.organization AND t2.name = t1.name ORDER BY t2.updated_at DESC NULLS LAST, t2.id DESC LIMIT 1) AS keeper_id FROM team t1 WHERE t1.id = tm.team_id ) subq ) WHERE EXISTS ( SELECT 1 FROM team t1 JOIN team t2 ON t1.organization = t2.organization AND t1.name = t2.name AND t1.id != t2.id WHERE t1.id = tm.team_id );
-- TeamRepositoryPermission references UPDATE team_repository_permission trp SET team_id = ( SELECT keeper_id FROM ( SELECT t1.organization, t1.name, (SELECT id FROM team t2 WHERE t2.organization = t1.organization AND t2.name = t1.name ORDER BY t2.updated_at DESC NULLS LAST, t2.id DESC LIMIT 1) AS keeper_id FROM team t1 WHERE t1.id = trp.team_id ) subq ) WHERE EXISTS ( SELECT 1 FROM team t1 JOIN team t2 ON t1.organization = t2.organization AND t1.name = t2.name AND t1.id != t2.id WHERE t1.id = trp.team_id );
-- WorkspaceTeamLabelFilter references UPDATE workspace_team_label_filter wtlf SET team_id = ( SELECT keeper_id FROM ( SELECT t1.organization, t1.name, (SELECT id FROM team t2 WHERE t2.organization = t1.organization AND t2.name = t1.name ORDER BY t2.updated_at DESC NULLS LAST, t2.id DESC LIMIT 1) AS keeper_id FROM team t1 WHERE t1.id = wtlf.team_id ) subq ) WHERE EXISTS ( SELECT 1 FROM team t1 JOIN team t2 ON t1.organization = t2.organization AND t1.name = t2.name AND t1.id != t2.id WHERE t1.id = wtlf.team_id );
-- WorkspaceTeamRepositorySettings references UPDATE workspace_team_repository_settings wtrs SET team_id = ( SELECT keeper_id FROM ( SELECT t1.organization, t1.name, (SELECT id FROM team t2 WHERE t2.organization = t1.organization AND t2.name = t1.name ORDER BY t2.updated_at DESC NULLS LAST, t2.id DESC LIMIT 1) AS keeper_id FROM team t1 WHERE t1.id = wtrs.team_id ) subq ) WHERE EXISTS ( SELECT 1 FROM team t1 JOIN team t2 ON t1.organization = t2.organization AND t1.name = t2.name AND t1.id != t2.id WHERE t1.id = wtrs.team_id );
-- Now delete duplicate teams, keeping the one with the latest updated_at DELETE FROM team WHERE id IN ( SELECT id FROM ( SELECT id, ROW_NUMBER() OVER ( PARTITION BY organization, name ORDER BY updated_at DESC NULLS LAST, id DESC ) AS rn FROM team WHERE organization IS NOT NULL AND name IS NOT NULL ) ranked WHERE rn > 1 );

-- Create a temp table mapping duplicate users to their keeper
-- Uses window function to identify duplicates and their keeper (newest by updated_at)
CREATE TEMP TABLE user_merge_map AS
SELECT
    duplicate_id,
    keeper_id
FROM (
    SELECT
        u.id AS duplicate_id,
        FIRST_VALUE(u.id) OVER (
            PARTITION BY u.login
            ORDER BY u.updated_at DESC NULLS LAST, u.id DESC
        ) AS keeper_id
    FROM "user" u
    WHERE u.login IN (
        SELECT login FROM "user" GROUP BY login HAVING COUNT(*) > 1
    )
) ranked
WHERE duplicate_id != keeper_id;

-- Only proceed if there are duplicates
DO $$
BEGIN
    IF EXISTS (SELECT 1 FROM user_merge_map) THEN
        -- Update issue.author_id
        UPDATE issue SET author_id = m.keeper_id
        FROM user_merge_map m WHERE issue.author_id = m.duplicate_id;

        -- Update issue.merged_by_id (for pull requests)
        UPDATE issue SET merged_by_id = m.keeper_id
        FROM user_merge_map m WHERE issue.merged_by_id = m.duplicate_id;

        -- Update issue_comment.author_id
        UPDATE issue_comment SET author_id = m.keeper_id
        FROM user_merge_map m WHERE issue_comment.author_id = m.duplicate_id;

        -- Update pull_request_review.author_id
        UPDATE pull_request_review SET author_id = m.keeper_id
        FROM user_merge_map m WHERE pull_request_review.author_id = m.duplicate_id;

        -- Update pull_request_review_comment.author_id
        UPDATE pull_request_review_comment SET author_id = m.keeper_id
        FROM user_merge_map m WHERE pull_request_review_comment.author_id = m.duplicate_id;

        -- Update pull_request_review_thread.resolved_by_id
        UPDATE pull_request_review_thread SET resolved_by_id = m.keeper_id
        FROM user_merge_map m WHERE pull_request_review_thread.resolved_by_id = m.duplicate_id;

        -- Update milestone.creator_id
        UPDATE milestone SET creator_id = m.keeper_id
        FROM user_merge_map m WHERE milestone.creator_id = m.duplicate_id;

        -- Update activity_event.actor_id
        UPDATE activity_event SET actor_id = m.keeper_id
        FROM user_merge_map m WHERE activity_event.actor_id = m.duplicate_id;

        -- Delete user_preferences for duplicate users (keeper keeps their own preferences)
        DELETE FROM user_preferences up
        WHERE EXISTS (SELECT 1 FROM user_merge_map m WHERE up.user_id = m.duplicate_id);

        -- Update chat_thread.user_id (mentor conversations)
        UPDATE chat_thread SET user_id = m.keeper_id
        FROM user_merge_map m WHERE chat_thread.user_id = m.duplicate_id;

        -- Update document.user_id (uploaded files)
        UPDATE document SET user_id = m.keeper_id
        FROM user_merge_map m WHERE document.user_id = m.duplicate_id;

        -- Delete duplicate team_membership entries (avoid PK conflict)
        DELETE FROM team_membership tm
        WHERE EXISTS (SELECT 1 FROM user_merge_map m WHERE tm.user_id = m.duplicate_id);

        -- Delete duplicate repository_collaborator entries (avoid PK conflict)
        DELETE FROM repository_collaborator rc
        WHERE EXISTS (SELECT 1 FROM user_merge_map m WHERE rc.user_id = m.duplicate_id);

        -- Update workspace_membership - transfer to keeper if no conflict, delete otherwise
        -- First, try to update non-conflicting memberships
        UPDATE workspace_membership wm SET user_id = m.keeper_id
        FROM user_merge_map m WHERE wm.user_id = m.duplicate_id
        AND NOT EXISTS (
            SELECT 1 FROM workspace_membership wm2
            WHERE wm2.workspace_id = wm.workspace_id AND wm2.user_id = m.keeper_id
        );
        -- Delete remaining duplicate workspace_memberships (where keeper already has membership)
        DELETE FROM workspace_membership wm
        WHERE EXISTS (SELECT 1 FROM user_merge_map m WHERE wm.user_id = m.duplicate_id);

        -- Update issue_assignee junction table
        UPDATE issue_assignee SET user_id = m.keeper_id
        FROM user_merge_map m WHERE issue_assignee.user_id = m.duplicate_id
        AND NOT EXISTS (
            SELECT 1 FROM issue_assignee ia2
            WHERE ia2.issue_id = issue_assignee.issue_id AND ia2.user_id = m.keeper_id
        );
        DELETE FROM issue_assignee ia
        WHERE EXISTS (SELECT 1 FROM user_merge_map m WHERE ia.user_id = m.duplicate_id);

        -- Update pull_request_requested_reviewers junction table
        UPDATE pull_request_requested_reviewers SET user_id = m.keeper_id
        FROM user_merge_map m WHERE pull_request_requested_reviewers.user_id = m.duplicate_id
        AND NOT EXISTS (
            SELECT 1 FROM pull_request_requested_reviewers pr2
            WHERE pr2.pull_request_id = pull_request_requested_reviewers.pull_request_id
            AND pr2.user_id = m.keeper_id
        );
        DELETE FROM pull_request_requested_reviewers pr
        WHERE EXISTS (SELECT 1 FROM user_merge_map m WHERE pr.user_id = m.duplicate_id);

        -- Now delete duplicate users
        DELETE FROM "user" WHERE id IN (SELECT duplicate_id FROM user_merge_map);
    END IF;
END $$;

-- Clean up temp table
DROP TABLE IF EXISTS user_merge_map;
